// === Simple Hello World Data Generator ===
// n8n入門用のシンプルなデータ生成Function

// 現在時刻の取得
const now = new Date();
const timestamp = now.toISOString();
const japaneseTime = now.toLocaleString('ja-JP', {
  timeZone: 'Asia/Tokyo',
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
});

// ランダムな実行番号生成（1-999）
const executionCount = Math.floor(Math.random() * 999) + 1;

// ランダムメッセージの配列
const messages = [
  "🎉 n8nワークフローが正常に実行されました！",
  "👋 Hello World from n8n!",
  "🤝 Claude Codeとn8nの連携テスト成功",
  "✨ シンプルなワークフローの完成です",
  "🚀 n8n入門ワークフローへようこそ！"
];

// ランダムにメッセージを選択
const selectedMessage = messages[Math.floor(Math.random() * messages.length)];

// 学習ポイントのヒント
const learningTips = [
  "💡 Function NodeでJavaScriptが実行できます",
  "📊 items[0].jsonでデータにアクセス",
  "🔄 return文で次のノードにデータを渡す",
  "🐛 console.logでデバッグ可能"
];

const tip = learningTips[Math.floor(Math.random() * learningTips.length)];

// 出力データの構造化
return [{
  json: {
    // 基本情報
    timestamp: timestamp,
    japaneseTime: japaneseTime,
    executionId: `exec_${executionCount}`,

    // メッセージ情報
    message: selectedMessage,
    learningTip: tip,

    // ワークフロー情報
    workflow: {
      name: "Simple Hello World",
      version: "1.0.0",
      type: "sample",
      complexity: "beginner"
    },

    // ステータス情報
    status: "success",
    nodeExecuted: "Data Generator",

    // 統計情報
    stats: {
      executionNumber: executionCount,
      totalNodes: 3,
      currentNode: 2
    }
  }
}];

// === Simple Hello World Log Output ===
// 生成されたデータを整形してログ出力

// 入力データの取得
const inputData = items[0].json;

// ログメッセージの整形
const separator = "=".repeat(50);
const logMessage = `
${separator}
🎯 n8n Simple Hello World - 実行完了！
${separator}

📅 実行時刻: ${inputData.japaneseTime}
🆔 実行ID: ${inputData.executionId}
💬 メッセージ: ${inputData.message}
💡 学習ヒント: ${inputData.learningTip}

📊 ワークフロー情報:
   名前: ${inputData.workflow.name}
   バージョン: ${inputData.workflow.version}
   難易度: ${inputData.workflow.complexity}

📈 実行統計:
   実行番号: #${inputData.stats.executionNumber}
   総ノード数: ${inputData.stats.totalNodes}
   現在のノード: ${inputData.stats.currentNode}/${inputData.stats.totalNodes}

✅ ステータス: ${inputData.status.toUpperCase()}

${separator}
🎉 おめでとうございます！n8nの基本操作を体験できました！
   次は他のノードタイプも試してみましょう。
${separator}
`;

// コンソールにログ出力
console.log(logMessage);

// 実行完了データの生成
const completionTime = new Date().toISOString();

// 最終出力データ
return [{
  json: {
    // 元のデータを継承
    ...inputData,

    // ログ情報追加
    log: {
      message: logMessage,
      outputTime: completionTime,
      consoleOutput: true
    },

    // 完了情報
    completion: {
      status: "completed",
      finalNode: true,
      success: true,
      executionTime: completionTime
    },

    // 学習達成情報
    achievements: [
      "✅ Manual Triggerの使用",
      "✅ Function Nodeでのデータ生成",
      "✅ JavaScriptコードの実行",
      "✅ Console.logの使用",
      "✅ JSONデータ構造の理解"
    ]
  }
}];
